import { BaseUrl, InquiryOptions } from './interfaces';
/**
 * Wraps the Inquiry iframe with embedded modal event / action handling.
 */
export default class Client {
    private options;
    private _isLoading;
    private _isOpen;
    private containerId;
    private container;
    private containerParent;
    private refIframe;
    constructor(options: InquiryOptions);
    render(): void;
    open(): void;
    cancel(force: boolean): void;
    destroy(): void;
    private onLoad;
    private onReady;
    private onComplete;
    private onCancel;
    get isLoading(): boolean;
    get isOpen(): boolean;
    /**
     * Gets the base URL for iframe communication. If the URL does not match the
     * iframe origin, messages posted from persona-verify will be ignored by
     * persona-web.
     */
    get baseUrl(): BaseUrl;
}
